index.htmlstyle.cssscript.js <pre><code>&lt;html lang="en"&gt;  
&lt;head&gt;  
    &lt;meta charset="UTF-8"&gt;  
    &lt;meta name="viewport" content="width=device-width, initial-scale=1"&gt;  
    &lt;title&gt;Document&lt;/title&gt;  
    &lt;link rel="stylesheet" href="style.css"&gt;  
&lt;/head&gt;  
&lt;body&gt;  
    &lt;div class="container"&gt;  
        &lt;div class="progress-container"&gt;  
            &lt;div id="progress"&gt;&lt;/div&gt;  
            &lt;div class="circle active"&gt;1&lt;/div&gt;  
            &lt;div class="circle"&gt;2&lt;/div&gt;  
            &lt;div class="circle"&gt;3&lt;/div&gt;  
            &lt;div class="circle"&gt;4&lt;/div&gt;  
        &lt;/div&gt;        &lt;button class="prev" disabled&gt;prev&lt;/button&gt;  
        &lt;button class="next"&gt;next&lt;/button&gt;  
    &lt;/div&gt;    &lt;script src="script.js"&gt;&lt;/script&gt;  
&lt;/body&gt;  
&lt;/html&gt;
</code></pre> <pre><code>:root {  
    --line-border-empty: #e0e0e0;  
    --line-border-fill: #34db90;  
}  


body {  
    margin: 0;  
    width: 100vw;  
    height: 100vh;  
    display: flex;  
    justify-content: center;  
    align-items: center;  
    font-family: sans-serif;  
    background-color: #f7f7fa;  
}  

.container {  
    text-align: center;  
}  

.progress-container {  
    display: flex;  
    width: 300px;  
    justify-content: space-between;  
    position: relative;  
}  

.progress-container:before {  
    content: '';  
    position: absolute;  
    height: 5px;  
    width: 100%;  
    top: 50%;  
    transform: translateY(-50%);  
    background-color: var(--line-border-empty);  
    z-index: -1;  
}  

#progress {  
    position: absolute;  
    height: 5px;  
    width: 0;  
    top: 50%;  
    transform: translateY(-50%);  
    background-color: var(--line-border-fill);  
    z-index: -1;  
    transition: width ease-out .5s;  
}  

.circle {  
    height: 30px;  
    width: 30px;  
    display: flex;  
    justify-content: center;  
    align-items: center;  
    background-color: white;  
    border: 3px solid var(--line-border-empty);  
    border-radius: 50%;  
    padding: 10px;  
    font-size: 14px;  
    color: gray;  
    transition: border-color ease-out .5s;  
}  

button {  
    color: white;  
    background-color: #2083ac;  
    border: none;  
    border-radius: 5px;  
    margin: 20px 10px;  
    padding: 5px 20px;  
    cursor: pointer;  
}  

button:active {  
    transform: scale(0.98);  
}  

button:disabled {  
    background-color: #383838;  
    cursor: not-allowed;  
}  

.circle.active {  
    border-color: var(--line-border-fill);  
}  

* {  
    box-sizing: border-box;  
}
</code></pre> <pre><code>const prev = document.querySelector('.prev');  
const next = document.querySelector('.next');  
const circles = document.querySelectorAll('.circle');  
const progress = document.querySelector('#progress')  

let cur = 0;  
const max_cur = 3;  
const min_cur = 0;  

next.addEventListener('click', () =&gt; {  
    cur + 1 &lt;= max_cur &amp;&amp; cur++;  
    update();  
})  

prev.addEventListener('click', () =&gt; {  
    cur - 1 &gt;= min_cur &amp;&amp; cur--;  
    update();  
})  

function update() {  
    circles.forEach(circle =&gt; circle.classList.remove('active'))  
    for (let i = 0; i &lt;= cur; i ++) {  
        circles[i].classList.add('active')  
    }  
    progress.style.width = (cur / max_cur * 100) + '%';  

    cur === max_cur ? next.disabled = true : next.disabled = false;  
    cur === min_cur ? prev.disabled = true : prev.disabled = false;  
}
</code></pre>

