nav {  
    position: absolute;  
    left: -100px;  
    bottom: 0;  
    transition: left 1s ease-in-out;  
}  

.container.nav-open+nav {  
    left: 0;  
}  

nav&gt;ul {  
    list-style: none;  
}  

nav&gt;ul&gt;li&gt;a {  
    text-decoration: none;  
    color: white;  
